INSERT INTO `game_makers` (`id`, `key`, `name`, `node_name`, `phonetic`, `rating`, `type`, `description`, `description_source`, `related_game_maker_id`, `created_at`, `updated_at`) VALUES
	(1, 'capcom', 'カプコン', 'カプコン', NULL, 0, 1, '株式会社カプコンは大阪に本社を構える日本の大手ゲーム会社。\r\nリアルタイム3Dの技術力が高く、世界で評価されている。\r\nアクションゲームに定評があり、「ストリートファイター」や「ロックマン」「モンスターハンター」、そしてホラーゲームの代表格「バイオハザード」シリーズを手掛ける。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-15 20:54:43'),
	(2, 'tecmo', 'テクモ', 'テクモ', NULL, 0, 1, 'テクモは日本のゲーム会社。\r\n2009年にコーエーと合併しコーエーテクモになった。\r\n「デッドオアアライブ」や「ニンジャガイデン」などが有名。\r\nホラーゲームでは「零シリーズ」が有名。', NULL, 197, '2012-03-25 12:40:48', '2024-07-15 21:01:12'),
	(3, 'nintendo', '任天堂', '任天堂', NULL, 0, 1, '任天堂株式会社は日本の大手ゲーム会社。\r\nゲームハード及び自社プラットフォーム向けのゲームソフトを多く手掛け、「マリオ」「ゼルダ」「ポケモン」など世界中で大ヒットを飛ばしている。\r\nホラーゲームの販売は少ないが、一時期「零」シリーズのパブリッシングをしていたこともあった。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-15 21:05:55'),
	(4, 'sce', 'ソニーコンピュータエンタテインメント', 'ソニーコンピュータエンタテインメント', NULL, 0, 1, 'コンピュータエンタテインメント（SCE）は日本のゲームプラットフォームメーカー。\r\nプレイステーションシリーズの開発及び販売を行っていた。\r\n2016年にソニー・ネットワークエンタテインメントインターナショナルと統合し、ソニーインタラクティブエンタテインメント（SIE）になった。', NULL, 113, '2012-03-25 12:40:48', '2024-07-20 19:33:19'),
	(8, 'from-software', 'フロム・ソフトウェア', 'フロム・ソフトウェア', NULL, 0, 1, 'フロム・ソフトウェアは日本の大手ゲーム会社。\r\n「アーマード・コア」シリーズや「天誅」シリーズ、「ソウル」シリーズが有名。\r\n特に「ソウル」シリーズ発売から難易度の高いゲームで世界中で人気となり、「SEKIRO」や「エルデン・リング」はゲームオブザイヤーを獲得するにまで至った。\r\nホラーゲームでは「エコーナイト」シリーズや「九怨-kuon-」などを手掛けていたが、「ソウル」シリーズのヒット以降はホラーゲームの開発には手を出していない様子。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-15 21:18:16'),
	(9, 'electronic-arts', 'Electronic Arts', 'Electronic Arts', NULL, 0, 1, 'Electronic Artsはアメリカに拠点を置く大手ゲーム会社。\r\n世界中に開発スタジオがあり、大規模に世界展開している。\r\n「FIFA」シリーズ、「バトルフィールド」シリーズ、「Apex Legends」などが有名。\r\nホラーゲームでは「ALONE IN THE DARK」シリーズ、「デッドスペース」シリーズなどを手掛ける。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-15 21:23:40'),
	(10, 'marvelous-e', 'マーベラスエンターテイメント', 'マーベラスエンターテイメント', NULL, 0, 1, 'マーベラスエンターテイメントは日本の大手ゲーム会社。\r\nアニメ、音楽などエンタテインメント事業を手掛ける。\r\n「牧場物語」シリーズ、「ルーンファクトリー」シリーズ、「閃乱カグラ」シリーズなどが有名。\r\n自社開発のみならず、他社開発ソフトのパブリッシングも多く手掛けている。\r\n2011年に、AQインタラクティブ、アメージングと3社で合併し、マーベラスAQLとなった。\r\n2014年には社名をマーベラスに変更した。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-16 20:23:10'),
	(11, 'ms', 'マイクロソフト', 'マイクロソフト', NULL, 0, 1, 'Microsoftはアメリカに拠点を置く、超大手ソフトウェアメーカー。\r\nWindowsやOfficeなどを手掛け、ゲーム以外のソフトウェア産業界では世界トップの会社。\r\nゲーム機では「XBOX」シリーズを開発・販売している。\r\n日本ではイマイチだが、主戦場の北米ではそれなりに売れている模様。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-15 21:28:12'),
	(12, 'sega', 'セガ', 'セガ', NULL, 0, 1, 'セガは日本の大手ゲーム会社。\r\n「メガドライブ」「セガサターン」「ドリームキャスト」などのゲーム機を展開していた。\r\nゲームソフトでは「ソニック」「バーチャファイター」「龍が如く」などが有名。\r\nドリームキャストの販売が低迷し経営が悪化。\r\n2004年にパチスロ業界から多角化を目指していたサミーと合併し、セガサミーとなった。\r\n持ち株会社がセガサミーホールディングスで、セガは子会社として残っている。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-17 09:45:10'),
	(13, 'nippon1', '日本一ソフトウェア', '日本一ソフトウェア', NULL, 0, 1, '日本一ソフトウェアは岐阜に拠点を置く日本のゲームデベロッパー、パブリッシャー。\r\nA～B級のゲームが多いが、一定のファンを獲得しているらしい。\r\nホラーゲームも多くリリースしている。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-15 20:51:33'),
	(14, 'bandai-namco-entertainment', 'バンダイナムコエンターテインメント', 'バンダイナムコ<br>\r\nエンターテインメント', NULL, 0, 1, 'バンダイナムコエンターテインメントは日本の大手ゲーム会社。\r\nアニメ、おもちゃなど総合エンタテインメント企業として名をはせている。\r\nゲームの代表作として「パックマン」や「エースコンバット」「アイドルマスター」などがある。\r\nホラーゲームでは「リトルナイトメア」のパブリッシャーでもある。\r\n2005年にバンダイとナムコが合併、その中でゲーム部門を統合したバンダイナムコゲームスとして誕生した。\r\n2015年にバンダイナムコゲームスに社名変更している。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-16 20:34:14'),
	(17, 'square-enix', 'スクウェア・エニックス', 'スクウェア<br>\r\nエニックス', NULL, 0, 1, '株式会社スクウェア・エニックスは日本の大手ゲームメーカー。\r\n2003年、スクウェアとエニックスが合併して誕生した。\r\n代表作は言わずもがな、「ファイナルファンタジー」や「ドラゴンクエスト」など。\r\nホラーゲームでは「パラサイト・イヴ」や「ナナシノゲエム」などを販売している。', NULL, NULL, '2012-03-25 12:40:48', '2024-06-12 09:24:13'),
	(18, 'takara', 'タカラ', 'タカラ', NULL, 0, 1, 'タカラは日本の玩具メーカー。\r\nおもちゃでは「リカちゃん人形」「チョロQ」、そして「トランスフォーマー」が有名。\r\nゲームではおもちゃを題材にしたゲームを主に販売し、「クラッシュバンディクー」の販売権も持っていた。\r\nホラーゲームは「・・・いる！」の販売を手掛けたのみ。\r\n2006年にトミーと合併し、「タカラトミー」となった。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-16 20:40:26'),
	(19, 'bandai', 'バンダイ', 'バンダイ', NULL, 0, 1, 'バンダイは日本の大手玩具メーカー。\r\n「ガンダム」「ドラゴンボール」「ウルトラマン」「仮面ライダー」など日本を代表するアニメ・特撮の玩具を販売していた。\r\nゲームではキャラクターIPものをメインで販売していた。\r\n特にPS1の「ゲゲゲの鬼太郎」は発売から十数年経ってからニコニコ動画等で怖いと評判になり、多くのゲーム実況者が実況プレイ動画をアップロードしていた。\r\n2006年、ナムコと経営統合しバンダイナムコホールディングスとなった。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-17 01:44:53'),
	(20, 'gae', 'GAE', 'GAE', NULL, 0, 1, 'GAEは日本のゲーム会社。\r\nかつてはコンシューマゲームを開発していたが、近年はモバイルゲームに注力しているらしい。\r\nホラーゲームでは「歸らずの森」を販売している。\r\n2009年にグローバル・A・エンタテインメントからGAEに社名変更した。\r\n2018年から公式X(Twitter)の更新が止まっており、2024年時点でホームページへのアクセスができない。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-17 09:25:58'),
	(21, 'spike', 'スパイク', 'スパイク', NULL, 0, 1, 'スパイクは日本のゲーム会社。\r\n「侍」シリーズや「ファイヤープロレスリング」シリーズが有名。\r\n倒産したヒューマンの受け皿となり、「トワイライトシンドローム」などのホラーゲームも販売していた。\r\n海外ソフトのローカライズも手掛けており、「Bioshock」や「The Darkness」のローカライズ・販売もしていた。\r\n2012年にチュンソフトと合併し、スパイク・チュンソフトとなった。', NULL, 117, '2012-03-25 12:40:48', '2024-07-17 02:02:24'),
	(22, 'compile-heart', 'コンパイルハート', 'コンパイルハート', NULL, 0, 1, 'コンパイルハートは日本のゲーム会社。\r\n「ネプティーヌ」シリーズが有名。\r\nホラーゲームでは「学校の怪談DS」を手掛ける。\r\n2006年にアイディアファクトリーの子会社として設立された。\r\n当初は旧コンパイルの元代表などを起用していたが、今はされていない模様。\r\n「ぷよぷよ」などの一部を除き旧コンパイルの版権を持っている。', NULL, 48, '2012-03-25 12:40:48', '2024-07-17 02:08:31'),
	(23, 'aq', 'AQインタラクティブ', 'AQインタラクティブ', NULL, 0, 1, 'AQインタラクティブは日本のゲーム会社。\r\n「ブルードラゴン」や「ロストオデッセイ」などの開発を担当していた。\r\n2011年、マーベラスエンターテイメントと合併し、マーベラスAQLとなった。', NULL, NULL, '2012-03-25 12:40:48', '2024-07-17 02:13:13'),
	(25, 'hudson', 'ハドソン', 'ハドソン', NULL, 0, 1, 'ハドソンは日本のゲーム会社。\r\n「ボンバーマン」「桃太郎電鉄」そして高橋名人が有名。\r\nNECと共同で「PCエンジン」の開発・販売も行っていた。\r\n2012年、コナミに吸収合併された。', NULL, 46, '2012-03-25 12:40:48', '2024-07-17 09:19:48'),
	(27, 'd3p', 'D3パブリッシャー', 'D3パブリッシャー', NULL, 0, 1, 'D3パブリッシャーは日本のゲーム会社。\r\n低価格でそこそこの品質のSIMPLEシリーズで多くのソフトを開発・販売している。\r\n特に「地球防衛軍」と「お姉チャンバラ」がヒットし、続編がフルプライスで販売されている。\r\n2014年にバンダイナムコエンターテインメントの完全子会社化となった。', NULL, 14, '2012-03-25 12:40:49', '2024-07-17 09:23:37'),
	(28, 'enix', 'エニックス', 'エニックス', NULL, 0, 1, 'エニックスは日本の大手ゲームメーカー。\r\n基本的にパブリッシャーであり、自社開発はしていなかった。\r\n日本を代表するPRG「ドラゴンクエスト」のメーカーとして有名（同タイトルの開発は主にチュンソフトが担っていた）。\r\n2003年にスクウェアと合併し、スクウェア・エニックスとなった。', NULL, 17, '2012-03-25 12:40:49', '2024-07-17 09:26:44'),
	(29, 'tonkin-house', 'トンキンハウス', 'トンキンハウス', NULL, 0, 1, 'トンキンハウスは東京書籍グループのゲームブランド。\r\n東京書籍、のちに東京書籍メディアファクトリーが開発・販売するゲームで用いられていたのブランド名の一つ。\r\n現在はゲーム業界から撤退している。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-17 09:33:13'),
	(30, 'summy', 'サミー', 'サミー', NULL, 0, 1, 'サミーは日本の遊戯メーカー。\r\nパチスロの開発・販売がメインだが、「ギルティギア」や「サムライスピリッツ」の一部タイトルを販売していた。\r\n2004年にセガと経営統合し、持ち株会社のセガサミーホールディングスとなった。\r\nサミー自体は子会社として継続している。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-17 09:44:42'),
	(31, 'atlus', 'アトラス', 'アトラス', NULL, 0, 1, 'アトラスは日本のゲーム会社。\r\n「真・女神転生」や「ペルソナ」シリーズが有名。\r\n2014年にセガの完全子会社となった。', NULL, 12, '2012-03-25 12:40:49', '2024-07-17 09:47:16'),
	(32, 'bamprest', 'バンプレスト', 'バンプレスト', NULL, 0, 1, 'バンプレストは日本のゲーム会社、もしくはグッズのブランド名。\r\nゲームソフトでは「スーパーロボット大戦」が有名。\r\n2008年に当時のバンダイナムコゲームスに吸収され、ゲーム会社としてのバンプレストは解散。\r\n以後は紆余曲折を経て、UFOキャッチャー等での景品グッズのブランド名として使用されている。', NULL, 14, '2012-03-25 12:40:49', '2024-07-17 10:06:40'),
	(33, 'interchannel-holon', 'インターチャネル・ホロン', 'インターチャネル・ホロン', NULL, 0, 1, 'インターチャネル・ホロンは日本のゲーム会社。\r\n主にノベルゲームの開発・販売をしていた。\r\n2010年にゲーム事業から撤退した。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-19 00:38:56'),
	(34, 'nowpro', 'ナウプロダクション', 'ナウプロダクション', NULL, 0, 1, 'ナウプロダクションは日本のゲーム会社。\r\n受託開発が主だが、自社パブリッシングタイトルも手掛けている。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-17 20:09:22'),
	(35, 'metro', 'メトロ', 'メトロ', NULL, 0, 1, 'メトロは日本のゲーム会社。\r\nコンシューマゲーム、モバイルゲーム、アーケードゲームの受託開発を主に行っている。\r\n初代アーケード版の「アイドルマスター」や「ウルトラマン」「仮面ライダー」など特撮作品のゲームタイトルなどの開発実績を持つ。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-17 20:15:09'),
	(36, 'atari', 'アタリ', 'アタリ', NULL, 0, 1, 'アタリはアメリカのゲーム会社。\r\n「ポン」で世界で初めて商業的に成功したアーケードゲームを作った会社として有名。\r\nまたハードの「アタリ2600」がヒットし、当時としては世界最大のゲーム会社となったものの、後に”アタリショック”により経営が悪化した。\r\nその後、紆余曲折を経て、現在でもゲームの開発・販売を行っている。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-17 20:32:16'),
	(37, 'xing-entertainment', 'エクシング・エンタテイメント', 'エクシング<br>エンタテイメント', NULL, 0, 1, 'エクシング・エンターテインメントはエクシングのゲームブランド。\r\n「JOYSOUND」と「UGA」を運営しているエクシングが、ゲーム事業で利用していたブランド名。\r\n現在はゲーム事業から撤退している。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-17 20:37:28'),
	(39, 'dimple', 'ディンプル', 'ディンプル', NULL, 0, 1, 'ディプルは日本のゲーム会社。\r\nアドベンチャーゲームの販売が多く、アダルトゲームの家庭用ゲーム移植も多く行っていた。\r\n2010年に解散した。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-17 20:43:23'),
	(40, 'outbreak', 'アウトブレイク', 'アウトブレイク', NULL, 0, 1, '', NULL, NULL, '2012-03-25 12:40:49', '2024-07-17 21:12:24'),
	(41, 'human', 'ヒューマン', 'ヒューマン', NULL, 0, 1, 'ヒューマンは日本のゲーム会社。\r\n「ファイヤープロレスリング」や、「クロックタワー」「トワイライトシンドローム」が有名。\r\n2000年に倒産し、スタッフやタイトルの権利はスパイクに吸収された。', NULL, 21, '2012-03-25 12:40:49', '2024-07-17 21:03:27'),
	(42, 'b-f', 'ビー・ファクトリー', 'ビー・ファクトリー', NULL, 0, 1, '', NULL, NULL, '2012-03-25 12:40:49', '2024-06-04 09:28:56'),
	(43, 'gaga', 'ギャガ・コミュニケーションズ', 'ギャガ・コミュニケーションズ', NULL, 0, 1, 'ギャガ・コミュニケーションズは日本の映画配給会社。\r\n「マスク」や「ハンニバル」などの映画を配給していた。\r\n2009年にギャガへ社名変更した。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-17 21:20:52'),
	(44, 'acclaim-japan', 'アクレイムジャパン', 'アクレイムジャパン', NULL, 0, 1, 'アクレイムジャパンはアメリカのゲーム会社「アクレイム・エンタテインメント」の日本法人。\r\n「モータルコンバット」や「NBAジャム」など、海外ゲームのローカライズと販売を行っていた。\r\n1999年に業務を終了、親会社も2004年に破産した。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-18 09:06:16'),
	(45, 'warp', 'ワープ', 'ワープ', NULL, 0, 1, 'ワープは日本のゲーム会社。\r\n「Dの食卓」や「エネミー・ゼロ」が有名。\r\n2000年にゲーム業界から撤退している。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-18 09:13:02'),
	(46, 'konami', 'コナミ', 'コナミ', NULL, 0, 1, 'コナミは日本の大手ゲーム会社。\r\n「メタルギア」「ときめきメモリアル」「パワフルプロ野球」「ウイニングイレブン」「遊戯王」などが有名。\r\nスポーツクラブの経営なども行っている。\r\n主に家庭用ゲームの開発・販売はコナミデジタルエンタテインメントが行っていますが、当サイトでは単に「コナミ」とします。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-18 09:20:15'),
	(47, 'yutaka', 'ユタカ', 'ユタカ', NULL, 0, 1, 'ユタカは日本のゲーム会社。\r\nバンダイの子会社としてガンダムや仮面ライダーなど、アニメ・特撮のゲームソフトを開発・販売していた。\r\n2003年にポピーに照合を変更、2007年にプレックスに吸収合併された。', NULL, 19, '2012-03-25 12:40:49', '2024-07-18 09:57:17'),
	(48, 'idea-factory', 'アイディアファクトリー', 'アイディアファクトリー', NULL, 0, 1, 'アイディアファクトリーは日本のゲーム会社。\r\n「スペクトラルフォース」や「ジェネレーションオブカオス」シリーズを手掛けていた。\r\n乙女ゲームブランドの「オトメイト」も展開している。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-18 10:02:36'),
	(49, 'success', 'サクセス', 'サクセス', NULL, 0, 1, 'サクセスは日本のゲーム会社。\r\n主に受託開発で多くのタイトルの開発に関わっている。\r\n「コットン」や「上海」シリーズを手掛けている。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-18 20:02:29'),
	(50, 'crazy', 'クレイジーゲーム', 'クレイジーゲーム', NULL, 0, 1, 'クレイジーゲームは日本のゲーム会社。\r\n「イルブリード」を含む3作品をリリースして解散した。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-18 09:36:04'),
	(51, 'jaleco', 'ジャレコ', 'ジャレコ', NULL, 0, 1, 'ジャレコは日本のゲーム会社。\r\nファミコン～スーパーファミコン時代にはゲーム業界の一角を担う存在だった。\r\n経営の悪化に伴い、2009年にゲームヤロウの完全子会社となるが2014年のゲームヤロウ破産と共に消滅した。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-18 09:44:39'),
	(52, 'media-entertainment', 'メディアエンターテイメント', 'メディアエンターテイメント', NULL, 0, 1, 'メディアエンターテイメントは日本のゲーム会社。\r\nパチスロ帝王シリーズやグルメシリーズを手掛けていた。\r\n2000年代半ばに活動を停止している。', NULL, NULL, '2012-03-25 12:40:49', '2024-07-18 09:48:22'),
	(53, 'cyber-front', 'サイバーフロント', 'サイバーフロント', NULL, 0, 1, 'サイバーフロントは日本のゲーム会社。\r\n「シヴィライゼーション」シリーズなど、海外ゲームのローカライズと販売を手掛けていた。\r\n2010年に加賀電子の子会社となったが、経営改善の兆しが見えず2013年に解散となった。', NULL, NULL, '2012-03-25 12:40:50', '2024-07-18 10:05:37'),
	(54, 'victor', 'ビクターインタラクティブソフトウエア', 'ビクターインタラクティブ<br>\r\nソフトウエア', NULL, 0, 1, 'ビクターインタラクティブソフトウェアは日本のゲーム会社。\r\n「牧場物語」シリーズなどを手掛けていた。\r\n2003年にマーベラスエンターテイメントに買収され、マーベラスインタラクティブに社名変更。\r\n2007年にマーベラスエンターテイメントに吸収合併された。', NULL, NULL, '2012-03-25 12:40:50', '2024-07-18 19:53:57'),
	(56, 'visit', 'ヴィジット', 'ヴィジット', NULL, 0, 1, 'ヴィジットは日本のゲーム会社。\r\nホラーノベルゲームをいくつか販売していた。\r\n現在は活動を停止している模様。', NULL, NULL, '2012-03-25 12:40:50', '2024-07-18 20:07:38'),
	(57, 'eidos', 'アイドス', 'アイドス', NULL, 0, 1, 'アイドスはイギリスのゲーム会社。\r\n「トゥームレイダー」や「ヒットマン」を手掛けていた。\r\n2009年にスクウェア・エニックスに買収され、Square Enix Europeの一部となった。', NULL, 17, '2012-03-25 12:40:50', '2024-07-18 20:11:03'),
	(58, 'kadokawa', '角川書店', '角川書店', NULL, 0, 1, '角川書店は日本の出版会社。\r\nゲーム事業も展開しており、自社出版書籍のゲームなどを販売していた。\r\n2013年にいくつかの子会社を吸収合併し、KADOKAWAとなった。', NULL, NULL, '2012-03-25 12:40:50', '2024-07-18 20:21:40'),
	(59, 'p-and-a', 'ピーアンドエー', 'ピーアンドエー', NULL, 0, 1, 'ピーアンドエ―は日本のソフトウェア会社。\r\n「QUAKE」など海外ゲームのローカライズと販売を行っていたが、ゲーム事業は徐々に縮小。\r\nディスクライティングソフトやPhotoshop用プラグインなどを販売していた。\r\n2014年にホームページが公開されなくなり、活動を停止している模様。', NULL, NULL, '2012-03-25 12:40:50', '2024-07-19 00:28:56'),
	(60, 'activision', 'アクティビジョン', 'アクティビジョン', NULL, 0, 1, 'アクティビジョンはアメリカの大手ゲーム会社。\r\n「Call of Duty」や「クラッシュ・バンディクー」などを手掛ける。\r\n2008年にブリザード・エンターテインメントと合併し、アクティビジョン・ブリザードとなった。\r\n2022年にマイクロソフトに買収されている。', NULL, NULL, '2012-03-25 12:40:50', '2024-07-19 00:31:52'),
	(61, 'livedoor', 'ライブドア', 'ライブドア', NULL, 0, 1, 'ライブドアは日本のインターネットサービス企業。\r\nプロバイダ事業やポータルサイト運営やブログサービスなどを展開し、現在もブログサービスなど一部は継続している。\r\n海外ゲームのローカライズ版の販売なども手掛けていた。', NULL, NULL, '2012-03-25 12:40:50', '2024-07-19 00:37:51'),
	(62, 'sega-a', 'Sega of America', 'Sega of America', NULL, 0, 1, '', NULL, NULL, '2012-03-25 12:40:50', '2024-06-04 09:28:56'),
	(64, 'innter-channel', 'インターチャネル', 'インターチャネル', NULL, 0, 1, 'インターチャネルは日本のゲーム会社。\r\n「センチメンタルグラフィティ」や「CLANNAD」「Kanon」などの家庭用ゲーム機移植を行っていた。\r\n2006年にホロンと合併し、「インターチャネル・ホロン」となった。', NULL, 33, '2012-03-25 12:40:50', '2024-07-19 00:40:59'),
	(65, 'pandora-box', 'パンドラボックス', 'パンドラボックス', NULL, 0, 1, 'パンドラボックスは日本のゲー会社。\r\nスーパーファミコンの「学校であった怖い話」などを手掛けていた。\r\nデベロッパーとしてスーパーファミコンからプレイステーションのゲーム開発を行っていた。\r\n独自タイトルの「パンドラMAXシリーズ」を販売するも売り上げは低迷、2000年初頭から活動を休止していた。\r\n2007年にシャノンへ社名変更し活動を再開した。', NULL, 179, '2012-03-25 12:40:50', '2024-07-19 00:49:18'),
	(66, 'genex', 'ジェネックス', 'ジェネックス', NULL, 0, 1, '', NULL, NULL, '2012-03-25 12:40:50', '2024-07-19 01:41:09'),
	(67, 'athene', 'アテナ', 'アテナ', NULL, 0, 1, 'アテナは日本のゲーム会社。\r\n「プロ麻雀 極」シリーズや「夜光虫」シリーズを手掛けていた。\r\n2013年に破産した。', NULL, NULL, '2012-03-25 12:40:50', '2024-07-19 01:43:58'),
	(69, 'zoo', 'ズー', 'ズー', NULL, 0, 1, 'ズーは日本のシステムおよびゲーム会社。\r\n医療とゲームの2つの事業を展開している。\r\n「S.T.A.K.E.R.」や「セインツロウ」など海外ゲームのローカライズと販売や、スマホ向けゲームの開発・販売を行っている。', NULL, NULL, '2012-03-25 12:40:50', '2024-07-19 01:53:36'),
	(70, 'liar-soft', 'ライアーソフト', 'ライアーソフト', NULL, 3, 1, 'ライアーソフトは日本のアダルトゲーム会社。\r\n「腐り姫」や「CANNONBALL 〜ねこねこマシン猛レース!〜」、「What a beautiful」シリーズなどを手掛ける。', NULL, NULL, '2012-03-25 12:40:50', '2025-10-15 07:04:26'),
	(71, 'fc-fc03', 'F&C FC03', 'F&C FC03', NULL, 3, 1, 'F&Cは日本のアダルトゲーム会社で、F&C FC03は同社が展開していたブランドの1つ。\r\n別ブランドだが、F&C FC02が「Pia♥キャロットへようこそ!!」などを手掛けている。\r\n2002年にF&C FC03が独立しDreamSoftとして活動していたが、2006年に休止している。', NULL, NULL, '2012-03-25 12:40:50', '2025-10-15 07:04:26'),
	(72, 'tigerman-project', 'タイガーマンプロジェクト', 'タイガーマンプロジェクト', NULL, 3, 1, 'タイガーマンプロジェクトは日本のアダルトゲーム会社「マリゴールド」が展開するブランド。\r\n「蛍子」の1作品のみを開発・販売した。', NULL, NULL, '2012-03-25 12:40:50', '2025-10-15 07:04:26'),
	(73, 'abogado-owers', 'アボガドパワーズ', 'アボガドパワーズ', NULL, 3, 1, 'アボガドパワーズは日本のアダルトゲーム会社「スケアクロウ」が展開するブランド。\r\n2005年に創業者の死去により活動を停止した。\r\n2007年に復活し、2014年には新作を発表したものの発売されないまま、事実上の活動停止状態にある。', NULL, NULL, '2012-03-25 12:40:50', '2025-10-15 07:04:26'),
	(75, 'lass', 'Lass', 'Lass', NULL, 0, 1, 'Lassは日本のアダルトゲーム会社ラズエルが展開する美少女ゲームブランド。\r\n2018年、ラズエルが破産し消滅した。', NULL, NULL, '2012-03-25 12:40:50', '2024-07-19 02:48:17'),
	(76, 'blue-gale', 'ブルーゲイル', 'ブルーゲイル', NULL, 3, 1, '', NULL, NULL, '2012-03-25 12:40:50', '2025-10-15 07:04:26'),
	(77, 'cs-ware', 'シーズウェア', 'シーズウェア', NULL, 3, 1, 'シーズシェアは日本のアダルトゲーム会社「姫屋ソフト」のブランド。\r\n「禁断の血族」「EVE」「DESIRE」シリーズなどを手掛ける。\r\n2003年に活動を停止した。', NULL, NULL, '2012-03-25 12:40:50', '2025-10-15 07:04:26'),
	(78, 'mbs-truth', 'MBS Truth', 'MBS Truth', NULL, 3, 1, 'MBS Truthは日本のアダルトゲーム会社「テックアーツ」が展開するブランド。\r\n会社は存在しているようだが、2016年から新作の発売やSNSの更新が止まっている。', NULL, NULL, '2012-03-25 12:40:50', '2025-10-15 07:04:26'),
	(79, 'black-cyc', 'BLACK CYC', 'BLACK CYC', NULL, 3, 1, 'BLACK CYCは日本のアダルトゲーム会社「ビーアイコミュニケーションズ」の堕落・鬼畜・陵辱系ブランド。\r\n2011年よりランダ・アミューズへ移管している。\r\n2016年発売のタイトル以降、新作は発売されていない。', NULL, NULL, '2012-03-25 12:40:50', '2025-10-15 07:04:26'),
