INSERT INTO `game_media_mixes` (`id`, `key`, `type`, `name`, `node_name`, `game_franchise_id`, `game_media_mix_group_id`, `rating`, `sort_order`, `description`, `description_source`, `ogp_cache_id`, `use_ogp_description`, `og_url`, `created_at`, `updated_at`) VALUES
	(1, '1', 1, 'バイオハザード ディジェネレーション', 'バイオハザード<br>\r\nディジェネレーション', NULL, 2, 0, 1, '忌まわしい事件から７年。\r\n惨劇は再びアメリカ中西部工場都市の空港で起ころうとしていた。\r\nバイオテロや薬害被害者救済を行うべく、ＮＧＯに所属していたクレア・レッドフィールドは空港利用客の中にゾンビを発見。\r\n警備員が噛まれたことを発端に続々と一般客や空港職員が感染していき空港内は大パニックとなる。\r\n時同じくして、空でも旅客機内でウィルス感染者が出たことで緊急着陸するべく空港へ向かったが、パニックになっているロビーへ激突してしまう。\r\nさらに機内から降りてきた乗組員や乗客らは皆ゾンビ化していた。\r\n一方、合衆国大統領は直轄のエージェント、レオン・Ｓ・ケネディに任務を受け、事態鎮圧の特別指揮官として空港へ向かわせるが・・・', '<a href="https://www.sonypictures.jp/he/882458" target="_blank">ソニー・ピクチャーズ公式</a>', 9, 1, NULL, '2024-05-19 20:48:45', '2024-09-19 00:10:21'),
	(2, 'otogirisou', 4, '弟切草', '弟切草', 20, NULL, 0, 1, '弟切草……その花言葉は『復讐』。\r\nゲームデザイナーの公平は、恋人奈美とのドライブで山中、事故に遭う。\r\n二人がやっとたどり着いたのは、弟切草が咲き乱れる洋館だった。\r\n「まるで俺が創ったゲームそのものだ！」愕然とする公平。\r\nそして、それは惨劇の幕開けだった……。', '<a href="https://amzn.to/3yKHeHk" target="_blank"><i class="bi bi-shop"></i>Kindleストア</a>', NULL, 0, NULL, '2024-07-10 09:35:50', '2024-08-19 11:37:38'),
	(3, 'biohazard_movie', 1, 'バイオハザード', 'バイオハザード', NULL, 1, 0, 1, '巨大企業アンブレラ社が地中深くに作り上げた秘密研究所ハイブ。\r\nここで開発中のウィルスが何者かの手によって空気中に漏洩した。\r\nメインコンピューターは汚染が地上に拡大するのを防ぐために研究所を封鎖。\r\nこれによってすべての所員の生命が失われた。\r\nアンブレラ社は特殊部隊を派遣するが、ハイブに進入した彼らを待っていたのは、ウィルスがもたらした戦慄すべき光景だった・・・！', '<a href="https://www.sonypictures.jp/he/745129" target="_blank">ソニー・ピクチャーズ公式</a>', 1, 1, NULL, '2024-08-09 09:24:42', '2024-09-19 00:05:59'),
	(4, 'biohazard2_apocalypse', 1, 'バイオハザードII アポカリプス', 'バイオハザードII<br>アポカリプス', NULL, 1, 0, 2, 'アンデッドの街と化したラクーンシティでアリスは生存者のジルたちと行動をともにしていた。\r\nそこへ、全ての元凶＜Ｔ-ウイルス＞を発明したアシュフォード博士から電話が入る。\r\n彼は街に核爆弾が投下されると告げ脱出を手引きする代わりに、娘の救出を要求する。\r\nさらに、背後には追跡者“ネメシス”の陰も忍び寄っていた。最悪の結末まで、あとわずか。果たして彼らは、無事に生き残れるのかー。', '<a href="https://www.sonypictures.jp/he/750238" target="_blank">ソニー・ピクチャーズ公式</a>', 2, 1, NULL, '2024-08-09 09:26:49', '2024-09-19 00:07:00'),
	(5, 'biohazard3', 1, 'バイオハザードIII', 'バイオハザードIII', NULL, 1, 0, 3, 'ラクーンシティでの惨劇から数年後、Ｔ－ウイルスの感染は世界中へ広がり、人類はアンデッドへ、地上世界は砂漠へと化していた。\r\nさらにアンブレラ社ではアリス計画が始動し、アリスのクローン実験が繰り返されていく、そんな中、独り世界を彷徨うアリスは、離れ離れになっていたカルロスたちと再会、そしてクレアと彼女が率いる武装集団も新たな仲間に加わり、燃料や食料を確保するため荒涼のラスベガスへ向かうのだが・・・。', '<a href="https://www.sonypictures.jp/he/831762" target="_blank">ソニー・ピクチャーズ公式</a>', 3, 1, NULL, '2024-08-09 09:27:38', '2024-09-19 00:07:26'),
	(6, 'biohazard4-after-life', 1, 'バイオハザードIV アフターライフ', 'バイオハザードIV<br>\r\nアフターライフ', NULL, 1, 0, 4, 'ウィルスによって破壊された世界で、ゾンビ達の犠牲者は数を増すばかり。\r\nアリスは生存者を捜しながら、安全な場所へと皆を避難させることに奔走していた。\r\nアンブレラ社とアリスの戦いは新たなステージを迎えていた。\r\nそんな中、アリスの旧友が現れる。\r\nゾンビ達から逃れ、新しい安息の地ロサンジェルスへと向かうが、そこは安息の地ではなくおびただしい数のゾンビ達がはびこる、死の街と化していた。\r\nアリス達はこうして罠へと足を踏み入れていく・・・', '<a href="https://www.sonypictures.jp/he/928827" target="_blank">ソニー・ピクチャーズ公式</a>', 4, 1, NULL, '2024-08-09 09:28:24', '2024-09-19 00:08:07'),
	(7, 'biohazard5-retribution', 1, 'バイオハザードV リトリビューション', 'バイオハザードV<br>リトリビューション', NULL, 1, 0, 5, 'アンブレラ社が開発したT－ウィルスが蔓延し、地球はアンデッドに覆い尽くされようとしていた。\r\n人類最後の希望であるアリスは、アンブレラ社に囚われ、ある極秘施設の中で目覚める。\r\nアリスはその巨大な施設から脱出しようとするが、気がつくと東京、ニューヨーク、モスクワなどの壊滅したはずの都市に移動している。\r\n実は、その施設には、世界を覆す驚くべき秘密が隠されていた。\r\nしかも、全ての背後には、アンブレラ社をも裏切った最凶の黒幕が・・・・・・。\r\n元仲間がアリスを攻撃し、かつての敵が手を差し伸べる。\r\nアリスは誰を信じ、何を疑えばいいのか？\r\n最終決戦に向けて、アリスの壮絶な戦いが始まる！', '<a href="https://www.sonypictures.jp/he/1110594" target="_blank">ソニー・ピクチャーズ公式</a>', 5, 1, NULL, '2024-08-09 09:29:29', '2024-09-19 00:08:35'),
	(8, 'biohazard-the-final', 1, 'バイオハザード: ザ・ファイナル', 'バイオハザード<br>\r\nザ・ファイナル', NULL, 1, 0, 6, '人工知能レッドクイーンとの戦いに敗れ、瓦礫の中で独り目を覚ましたアリス。\r\nそんな彼女の前に廃墟のコンピューターを通じてレッドクイーンが現れ、48時間以内に人類は滅びること、そしてそれを食い止める方法を告げる。\r\n敵からの思いがけない言葉に半信半疑のまま、アリスはラクーンシティへと向かう。\r\n滅亡へのタイムリミットが刻一刻と迫る中、命を捨てて最後の戦いに身を投じるアリス。\r\nやがて明らかになる、彼女にまつわる重大な秘密とは？\r\n想像を絶するファイナル・ファイトの果てに見えるのは、希望か、それとも絶望か……!?', '<a href="https://www.sonypictures.jp/he/1127103" target="_blank">ソニー・ピクチャーズ公式</a>', 6, 1, NULL, '2024-08-09 09:30:18', '2024-09-19 00:09:02'),
	(9, 'biohazard-damnation', 1, 'バイオハザード ダムネーション', 'バイオハザード<br>ダムネーション', NULL, 2, 0, 2, '旧ソビエトの社会体制崩壊後、独立し主権国家となった東スラブ共和国。\r\nしかしその後、富裕層（オルガルヒ）の支援を受ける政府側と、貧困層の民衆で構成される反政府勢力側との間で紛争が繰り返されていた。\r\nこの内乱が過激化する中、各地で「戦場でモンスターを見た」という奇妙な噂が流れ始めるのだった…。\r\nこの情報を受けた米国はB.O.W.拡散を危惧し、大統領直属エージェント、レオン・Ｓ・ケネディを現地に単独潜入させる。\r\nだが、政府事情により米国は突如撤退を決め、レオンにも退去命令が下る。\r\n 「B.O.W.の手掛かりがここにある以上、俺は残る―。」\r\n米国政府のサポートない中、独り戦場へ駆け出すレオンだったが、リッカー（B.O.W.）が現れ、反政府勢力側に拉致されてしまう。\r\n反政府勢力のメンバーであるアレクサンドル（サーシャ）・コザチェンコ　／バディ（相棒）とJD、長老会議の指導者アタマン（長老）であるイワン・ジュダノビッチ、そして椅子に縛られたレオン。\r\n絶体絶命の状況の中、政府軍の攻撃が開始される―。', '<a href="https://www.sonypictures.jp/he/1074920" target="_blank">ソニー・ピクチャーズ公式</a>', 7, 1, NULL, '2024-08-09 09:50:12', '2024-09-19 00:09:22'),
	(10, 'biohazard-vendetta', 1, 'バイオハザード: ヴェンデッタ', 'バイオハザード<br>ヴェンデッタ', NULL, 2, 0, 3, '対バイオテロ組織「BSAA」のクリス・レッドフィールドは、国際指名手配犯であるグレン・アリアスを追っていたが目前で逃してしまう\r\n。一方、元ラクーン市警「S.T.A.R.S.」の一員レベッカ・チェンバースは、ある不可解な事件の調査に大学教授として携わっていたが、突如研究所が襲撃され、死の危険にさらされてしまう。\r\n大統領直轄のエージェント組織「DSO」所属のレオン・Ｓ・ケネディと再会を果たしたクリスは、アリアスの真の目的が「バイオテロ」だと掴み、それを阻止すべく共にニューヨークへと向かうのであった。', '<a href="https://www.sonypictures.jp/he/2020277" target="_blank">ソニー・ピクチャーズ公式</a>', 8, 1, NULL, '2024-08-09 09:51:43', '2024-09-19 00:09:54'),
	(11, 'biohazard-death-island', 1, 'バイオハザード: デスアイランド', 'バイオハザード<br>デスアイランド', NULL, 2, 0, 4, 'アメリカ大統領直属のエージェントのレオンは、機密情報を握るアントニオ・テイラーを拉致した武装集団の車両を追っていた。\r\nだが突如現れた謎の女の妨害に遭い、犯人たちを取り逃がしてしまう。\r\n一方、対バイオテロ組織「BSAA」のクリスとジル、そしてアドバイザーのレベッカは、サンフランシスコを中心に起きている、感染経路不明のゾンビ発生事件を担当していた。\r\nクレアが勤める「テラセイブ」の調査協力の結果、ウィルスの被害者全員が監獄島・アルカトラズを訪れていたことを知り、クリスたちは島へと向かうが…。', '<a href="https://www.sonypictures.jp/he/1116907" target="_blank">ソニー・ピクチャーズ公式</a>', 10, 1, NULL, '2024-08-09 09:52:53', '2024-09-19 00:10:43'),
	(12, 'biohazard-welcome-to-racoon-city', 1, 'バイオハザード ウェルカム・トゥ・ラクーンシティ', 'バイオハザード<br>\r\nウェルカム・トゥ・ラクーンシティ', NULL, 3, 0, 1, '製薬会社アンブレラ社の拠点があるラクーンシティ。\r\nこの街の養護施設で育った主人公クレア・レッドフィールドは、アンブレラ社がある事故を起こしたことで、街に異変が起きていると警告する不可解なメッセージを受け取り、ラクーンシティへと戻ってきた。\r\nR.P.D.（ラクーン市警）の兄クリス・レッドフィールドはクレアの言うことをありえない陰謀論とあしらうが、やがて二人は街中を彷徨う住民たちの変わり果てた姿を目の当たりにする。次々と襲い掛かってくる住民たち。\r\nそんな中、アンブレラ社が秘密裏に人体実験を行ってきたことが徐々に明らかになっていく…。', '<a href="https://www.sonypictures.jp/he/11245088" target="_blank">ソニー・ピクチャーズ公式</a>', 22, 1, NULL, '2024-08-09 09:59:49', '2024-09-19 00:32:57'),
	(13, 'biohazard-drama', 5, 'バイオハザード', 'バイオハザード', NULL, 4, 0, 1, 'ウィルスのまん延により、世界が終末を迎えて十数年。\r\nジェイド・ウェスカーは凶暴な感染者をかわしながら、この大惨事を招いた責任者たちに鉄槌を下すと心に誓う。', '<a href="https://www.netflix.com/jp/title/80996532" target="_blank">Netflix</a>', 24, 1, NULL, '2024-08-09 21:11:43', '2024-09-20 11:08:06'),
	(14, 'biohazard-infinit-darkness', 5, 'バイオハザード: インフィニット ダークネス', 'バイオハザード<br>インフィニット<br>ダークネス', NULL, 5, 0, 1, '2006年、ホワイトハウスの極秘ファイルへの不正アクセス事件が発生。\r\n捜査のため召集されたレオンや、“ペナムスタンの英雄”ジェイソン達は、突如停電したホワイトハウス内で正体不明のゾンビ達と遭遇する。\r\n一方、クレアは難民支援でペナムスタンに在留中、ある少年が描いた奇妙な絵と出会う。\r\nそして、ウィルス感染者を描いたと思われるその絵をきっかけに、ペナムスタン内戦時に実施された、ある恐ろしい実験に辿り着く。\r\n一連の事件の真相に近づくレオンとクレア、そして更に平和を覆す恐怖の発露が迫っていることが発覚する…。', '<a href="https://www.amazon.co.jp/dp/B0B8PK8JWP?&linkCode=sl2&tag=h-g-s-22&linkId=f27a72f0ebad3c808073b5c2aed2cd3c&language=ja_JP&ref_=as_li_ss_tl" target="_blank"><i class="bi bi-shop"></i>Prime Video</a>', 25, 1, NULL, '2024-08-14 11:20:48', '2024-09-20 11:54:03'),
	(15, 'biohazard-heavenly-island', 2, 'バイオハザード ～ヘヴンリーアイランド～1', 'バイオハザード<br>～ヘヴンリーアイランド～<br>第1巻', NULL, 6, 0, 1, '水着タレントが競い合う、世界的TV番組「アイドル・サバイバル」の撮影隊が南米の孤島に到着。\r\nしかしその島には「クレア・レッドフィールド」も調査に訪れていて…。', '<a href="https://www.akitashoten.co.jp/comics/4253131212" target="_blank">秋田書店</a>', 17, 0, NULL, '2024-08-29 10:59:18', '2024-09-19 00:26:35'),
	(16, 'biohazard-heavenly-island2', 2, 'バイオハザード ～ヘヴンリーアイランド～2', 'バイオハザード<br>～ヘヴンリーアイランド～<br>第2巻', NULL, 6, 0, 2, 'クレアは、その孤島がt-ウイルスの生みの親・スペンサーの研究施設だと知りイネスの携帯を鳴らすが、イネスは島の神殿に祀られた謎の有機生体兵器に…。\r\n一方、TV番組「アイドル・サバイバル」撮影中に突如乱入してきた謎の怪人をきっかけに、水着タレントたちは次々とウイルス感染でゾンビ化し、撮影隊ベースのあるビーチはもはや…。', '<a href="https://www.akitashoten.co.jp/comics/4253131220" target="_blank">秋田書店</a>', 18, 0, NULL, '2024-08-29 11:24:51', '2024-09-19 00:27:01'),
	(17, 'biohazard-heavenly-island3', 2, 'バイオハザード ～ヘヴンリーアイランド～3', 'バイオハザード<br>～ヘヴンリーアイランド～<br>第3巻', NULL, 6, 0, 3, 'パンデミックによりゾンビで溢れかえったその孤島はまさに地獄絵図。\r\n逃げ惑う水着タレントたちをよそに、謎の怪人“銛男”にB.O.W.“スクリュー魔人”が追い討ちをかける。\r\nそして、死地からの救難信号を受けたクレアは、ついにBSAA・クリスに応援を要請し…。', '<a href="https://www.akitashoten.co.jp/comics/4253131239" target="_blank">秋田書店</a>', 19, 0, NULL, '2024-08-29 11:27:04', '2024-09-19 00:27:24'),
	(18, 'biohazard-heavenly-island4', 2, 'バイオハザード ～ヘヴンリーアイランド～4', 'バイオハザード<br>～ヘヴンリーアイランド～<br>第4巻', NULL, 6, 0, 4, 'BSAA・パーカーから渡されたメモを頼りに、クレアたちは島に隠された地下施設へ。\r\nそこはアンブレラの総帥・スペンサーが秘密裏に建設した、広大な生物兵器開発実験施設だった。\r\nそして、そこに眠る“禍々しき秘宝”を巡り様々な思惑が…!?', '<a href="https://www.akitashoten.co.jp/comics/4253131247" target="_blank">秋田書店</a>', 20, 0, NULL, '2024-08-29 11:28:33', '2024-09-19 00:28:01'),
	(19, 'biohazard-heavenly-island5', 2, 'バイオハザード ～ヘヴンリーアイランド～5', 'バイオハザード<br>～ヘヴンリーアイランド～<br>第5巻', NULL, 6, 0, 5, '孤島で秘密裏に進められていた最強生物を生み出す“蠱毒計画”は、“スクリュー魔人”を倒した“モリオ”が最終生存個体となり、いよいよ完成へ。\r\nしかし、そこに女優だったはずの紫麗が…!?\r\n禍々しき壮大な悪夢に巻き込まれた、クレアとトミナガたちの最終決戦の行方は…!!?', '<a href="https://www.akitashoten.co.jp/comics/4253131255" target="_blank">秋田書店</a>', 21, 0, NULL, '2024-08-29 11:30:05', '2024-09-19 00:28:25'),
	(20, 'biohazard-marhawa-desire1', 2, 'バイオハザード ～マルハワデザイア～1', 'バイオハザード<br>～マルハワデザイア～<br>第1巻', NULL, 7, 0, 1, 'アジア最大の名門校・マルハワ学園で、一人の女子生徒が謎のゾンビ化を遂げる…。\r\n調査に訪れたダグ教授と、その助手である甥のリッキーは、次第に拡大するその惨劇に巻き込まれ…。\r\n惨劇の裏で不気味に微笑む謎の人物。\r\nそしてダグの足取りを追うクリス・レッドフィールドらBSAAメンバー。偶然に絡み合った糸は必然の悪夢を紡ぎ…。', '<a href="https://www.akitashoten.co.jp/comics/425313291X" target="_blank">秋田書店</a>', 12, 0, NULL, '2024-08-30 12:01:58', '2024-09-19 00:24:13'),
	(21, 'biohazard-marhawa-desire2', 2, 'バイオハザード ～マルハワデザイア～2', 'バイオハザード<br>～マルハワデザイア～<br>第2巻', NULL, 7, 0, 1, 'リッキーとダグは、レイ、カプールと共にバイオハザードの発生した、地下制御室へと向かう。\r\nそこで得た“フードの女”と呼ばれる、バイオテロ重要参考人の名前。\r\n一人、また一人と犠牲者の増える最中、その“フードの女”がついに目の前に現れる。\r\nそしてその正体が明らかとなるが、同時に事態はより一層絶望的に…。', '<a href="https://www.akitashoten.co.jp/comics/4253132928" target="_blank">秋田書店</a>', 13, 0, NULL, '2024-08-30 12:06:35', '2024-09-19 00:24:57'),
	(22, 'biohazard-marhawa-desire3', 2, 'バイオハザード ～マルハワデザイア～3', 'バイオハザード<br>～マルハワデザイア～<br>第3巻', NULL, 7, 0, 1, 'ついに学園内バイオハザードの首謀者が明らかとなると同時に、学園内はパンデミックの地獄絵図に…。\r\nそして絶望するリッキーの前に…!?', '<a href="https://www.akitashoten.co.jp/comics/4253132936" target="_blank">秋田書店</a>', 14, 0, NULL, '2024-08-30 12:07:24', '2024-09-19 00:25:19'),
	(23, 'biohazard-marhawa-desire4', 2, 'バイオハザード ～マルハワデザイア～4', 'バイオハザード<br>～マルハワデザイア～<br>第4巻', NULL, 7, 0, 1, 'パンデミック状態でゾンビあふれかえる学園に、ついにクリス、ピアーズ、メラのBSAA(対バイオテロ部隊)隊員が到着。\r\n絶望に暮れるリッキーの前に一縷の望みが見えたはずだったが…!?', '<a href="https://www.akitashoten.co.jp/comics/4253132944" target="_blank">秋田書店</a>', 15, 0, NULL, '2024-08-30 12:08:01', '2024-09-19 00:29:53'),
	(24, 'biohazard-marhawa-desire5', 2, 'バイオハザード ～マルハワデザイア～5', 'バイオハザード<br>～マルハワデザイア～<br>第5巻', NULL, 7, 0, 1, 'ナナンの触手とゾンビガスによる、未体験の戦闘に苦戦を強いられるクリス。\r\nそして、その激闘の中に現れた学園内パンデミックの元凶“フードの女”…!?\r\n最終決戦の行方と、事件の全貌解明は……。', '<a href="https://www.akitashoten.co.jp/comics/4253132952" target="_blank">秋田書店</a>', 16, 0, NULL, '2024-08-30 12:09:05', '2024-09-19 00:26:10'),
	(25, 'zero-novel', 4, '零 〜ゼロ〜 女の子だけがかかる呪い', '零 〜ゼロ〜<br>女の子だけがかかる呪い', NULL, 8, 0, 1, '', NULL, 85, 1, NULL, '2024-10-11 20:45:13', '2024-10-11 21:13:17'),
	(26, 'zero-movie', 1, '劇場版 零～ゼロ～', '劇場版 零～ゼロ～', NULL, 8, 0, 1, '閉塞感漂う山間の町にある女子学園。\r\nアヤが寄宿舎の部屋に籠るようになってから数日後、生徒が次々と失踪する事件が起こる。\r\n姿を消した生徒たちは皆、アヤにそっくりな少女が写った１枚の写真がきっかけで、アヤの“幻”に悩まされていた。\r\n“幻”は耳元で囁きかける―「お願い、私の呪いを解いて…」―。\r\n学園には、午前零時に、女の子だけにかかる呪いがあるという言い伝えがあった。\r\nその頃、アヤの“幻”を見るようになっていたミチの前に、本物のアヤが姿を現わし、事件の謎に迫ろうとするが―――', '<a href="https://amzn.to/4dM6ifo" target="_blank"><i class="bi bi-shop"></i>Prime Video</a>', NULL, 0, NULL, '2024-10-11 20:48:55', '2024-10-11 21:00:52'),
	(27, 'alone-in-the-dark-movie', 1, 'アローン・イン・ザ・ダーク', 'アローン・イン・ザ・ダーク', NULL, 9, 0, 1, '超自然現象の調査員エドワード・カーンビーが、失踪した友人の行方を追い、秘密結社が関わる邪悪な陰謀に巻き込まれる物語である。\r\nエドワードは考古学者アリーヌと共に、恐ろしいクリーチャーや謎の存在と対峙しながら、古代の悪霊が蘇る危機を阻止するために奮闘する。', 'ChatGPT', NULL, 0, NULL, '2024-10-11 21:22:42', '2024-10-11 21:22:42'),
	(28, 'alone-in-the-dark2-movie', 1, 'アローン・イン・ザ・ダーク2', 'アローン・イン・ザ・ダーク2', NULL, 9, 0, 1, '超自然現象の調査員エドワード・カーンビーが、伝説の呪われた短剣にまつわる事件に挑む。\r\n短剣を手にした者が不死の力を得る一方で、恐ろしい魔女エリザベスが復活し、破壊をもたらそうとする。\r\nエドワードは仲間たちと共に魔女の計画を阻止し、短剣の秘密を解明するために危険な戦いに身を投じる。', 'ChatGPT', NULL, 0, NULL, '2024-10-11 21:23:49', '2024-10-11 21:23:49'),
	(29, 'parasite-eve-novel', 4, '[小説]パラサイト・イヴ', 'パラサイト・イヴ', NULL, 10, 0, 1, '事故で亡くなった愛妻の肝細胞を密かに培養する生化学者・利明。Eve1と名付けられたその細胞は、恐るべき未知の生命体へと変貌し、利明を求めて暴走をはじめる――。', '<a href="https://www.shinchosha.co.jp/book/121434/" target="_blank">『パラサイト・イヴ』 瀬名秀明 | 新潮社</a>', NULL, 0, NULL, '2024-10-15 07:43:28', '2024-10-15 08:44:55'),
	(30, 'parasite-eve-movie', 1, '[映画]パラサイト・イヴ', 'パラサイト・イヴ', NULL, 11, 0, 90, '生物学者の永島（三上博史）は人間の細胞の中にあるミトコンドリアの研究をしていたが、そんな折、妻の聖美（葉月里緒菜）が交通事故で脳死状態に。\r\n悲しみのあまり狂気の域に陥った永島は、聖美の肝臓を譲り受け、その細胞に“Eve1”と名づけて培養実験を開始するのだが……。', '<a href="https://amzn.to/4dSw69Q" target="_blank"><i class="bi bi-shop"></i>Amazon</a>', NULL, 0, NULL, '2024-10-15 08:36:45', '2024-10-15 08:47:02'),
	(31, 'the-3rd-birthday', 6, 'The 3rd Birthday', 'The 3rd Birthday', NULL, 12, 0, 1, '喪失の過去から目覚めた一人の女性、アヤ・ブレア。\r\n遥か未来より人類を食いつくし、時間を逆走して現代へと到達した未知なる生物“ツイステッド”を倒しうる唯一の方法、「オーバーダイブ」を使って捜査に乗り出す。自身の過去、そして真実へと向かって――。', '<a href="https://www.jp.square-enix.com/game/detail/3rd_birthday/" target="_blank">ザ・サード バースデイ | SQUARE ENIX</a>', 107, 0, NULL, '2024-10-15 08:54:46', '2024-10-15 08:54:46'),
	(32, 'ring-novel', 4, 'リング', 'リング', NULL, 14, 0, 1, '一本のビデオテープを観た四人の少年少女が、同日同時刻に死亡した\r\n。この忌まわしいビデオの中には、一体どんなメッセージが……\r\n恐怖とともに、未知なる世界へと導くオカルト・ホラーの金字塔。', '<a href="https://www.kadokawa.co.jp/product/199999188001/" target="_blank">角川ホラー文庫</a>', 133, 0, NULL, '2024-10-18 21:24:03', '2024-10-18 21:24:03'),
	(33, 'rasen-novel', 4, 'らせん', 'らせん', NULL, 14, 0, 1, '幼い息子を海で亡くした監察医の安藤は、謎の死を遂げた友人・高山の解剖を担当し、冠動脈から正体不明の肉腫を発見した。\r\n遺体からはみ出した新聞に書かれた数字は「リング」という言葉を暗示していた。', '<a href="https://www.kadokawa.co.jp/product/199999188003/" target="_blank">角川ホラー文庫</a>', 134, 0, NULL, '2024-10-18 21:25:31', '2024-10-18 21:25:31'),
	(34, 'loop-novel', 4, 'ループ', 'ループ', NULL, 14, 0, 3, '世界的に流行するガンウィルスの謎を解くため、バイクでアメリカの大地に乗り出した。\r\nそこで見たものは人類が未だ到達しえなかった究極のビジョン。\r\nリング・らせんに続く三部作の完結編。', '<a href="https://www.kadokawa.co.jp/product/199999188006/" target="_blank">角川ホラー文庫</a>', 135, 0, NULL, '2024-10-18 21:26:55', '2024-10-18 21:26:55'),
	(35, 'birthday-novel', 4, 'バースデイ', 'バースデイ', NULL, 14, 0, 4, '同日の同時刻に苦悶と驚愕の表情を残して四人の少年少女が死亡した。\r\nこの事件からすべてが始まったリングワールド。\r\nその三部作における女性たちの闘いを描いた、恐怖と感動の外伝的完結編。', '<a href="https://www.kadokawa.co.jp/product/199999188007/" target="_blank">角川ホラー文庫</a>', 136, 0, NULL, '2024-10-18 21:28:08', '2024-10-18 21:28:08'),
	(36, 's-novel', 4, 'エス', 'エス', NULL, 14, 0, 5, '中年男の首吊り自殺の模様を中継した不気味な動画。\r\nその真偽の解析を依頼された安藤孝則は動画の中の男が少しずつ不気味に変化していることに気付くが……!?\r\n「リング」シリーズ最新作。新たな恐怖の胎動！', '<a href="https://www.kadokawa.co.jp/product/321301000047/" target="_blank">角川ホラー文庫</a>', 137, 0, NULL, '2024-10-18 21:29:17', '2024-10-18 21:29:17'),
	(37, 'tide-novel', 4, 'タイド', 'タイド', NULL, 14, 0, 6, '高山竜司と二見馨という二人の男の人生を生きた記憶を持つ予備校講師の柏田誠二は、生徒から持ち込まれた相談をきっかけに貞子の呪いの真実を知り、自らの役割を自覚する……『リング』から続く怨念の正体とは!?', '<a href="https://www.kadokawa.co.jp/product/321511000305/" target="_blank">角川ホラー文庫</a>', 138, 0, NULL, '2024-10-18 21:31:04', '2024-10-18 21:31:04'),
	(38, 'kage-ga-iku-novel', 4, '影が行く', '影が行く', NULL, 17, 0, 1, '南極の凍てついた基地で、科学者たちが発見したのは、永遠に眠っていたはずの“それ”。\r\n解凍された謎の生命体は、ただのエイリアンではない。\r\n誰にでも、何にでも化ける…\r\nそして、完全にコピーする能力を持つ恐怖の存在だ。\r\n\r\n基地内で次々と姿を変え、仲間に紛れ込んだ“影”は誰なのか？\r\n信頼が崩れ、不信と狂気が広がる中、次に狙われるのはお前かもしれない…。\r\n\r\n逃げ場のない極寒の地で、生存者は何人残るのか？', 'ChatGPT', NULL, 0, NULL, '2024-10-19 21:43:09', '2024-10-19 21:43:09'),
	(39, 'yuusei-yorino-buttai-x', 1, '遊星よりの物体X', '遊星よりの物体X', NULL, 18, 0, 1, 'アラスカに謎の飛行物体が墜落し、米軍から派遣された捜索隊は氷に埋もれた巨大な円盤を発見。\r\n熱爆弾で掘り起こそうとした円盤は爆発飛散するが、人の姿のような「物体」が放り出され、一行はそれを基地に持ち帰った。\r\nその夜、蘇った「物体」が監視兵を襲い、犬3頭を殺害して逃走。\r\n犬に噛み千切られた残骸の一部を調べた結果、「物体」は宇宙より飛来した植物組織の生物で、動物の血を吸って成長し、知力と腕力は人類より優れた怪物であることを突き止める。', '<a href="https://amzn.to/4eWtiKm" target="_blank"><i class="bi bi-shop"></i>Amazon</a>', NULL, 0, NULL, '2024-10-22 20:14:37', '2024-10-22 20:14:37'),
	(40, 'yuusei-karano-buttai-x', 1, '遊星からの物体X', '遊星からの物体X', NULL, 19, 0, 1, '1982年の冬、南極基地にいる12人の隊員が10万年以上も氷の中に埋まっていたエイリアンを発見する。\r\n氷が溶け出てきたエイリアンは、次々と形態を変えながら隊員たちに襲いかかる。', '<a href="https://amzn.to/3AdFzea" target="_blank"><i class="bi bi-shop"></i>PrimeVideo</a>', NULL, 0, NULL, '2024-10-22 21:03:35', '2024-10-23 20:36:12'),
	(41, 'yuusei-karano-buttai-x-first-contact', 1, '遊星からの物体X ファーストコンタクト', '遊星からの物体X<br>\r\nファーストコンタクト', NULL, 19, 0, 1, '１９８２年。\r\nコロンビア大学の古生物学者ケイトは調査依頼を受け、南極のノルウェー基地を訪れる。\r\nそこで彼女が目にしたものは、地中から発掘された宇宙船と氷漬けになったエイリアンの遺体だった。\r\n早速、遺体の調査が開始されるが、突然甦ったエイリアンが隊員たちを襲い始める。\r\nしかもそれは、次々と人間の身体に寄生して姿を隠す性質を持っていた。\r\n誰がエイリアンかわからない状況の中、ケイトたちは疑心暗鬼を深めてゆく…。', '<a href="https://amzn.to/3Ab8Upu" target="_blank"><i class="bi bi-shop"></i>PrimeVideo</a>', NULL, 0, NULL, '2024-10-23 20:39:28', '2024-10-23 20:39:28'),
	(42, 'siren-forbidden-siren', 1, 'サイレン 〜FORBIDDEN SIREN〜', 'サイレン<br>\r\n〜FORBIDDEN SIREN〜', 6, NULL, 0, 1, '29年前。\r\n謎のサイレンの音とともに全住民消失事件が発生した孤島。\r\n一夜にして無人島化するという、この未曾有の怪事件から29年が経ち、現在平静を取り戻したこの島に引っ越してきた少女が、サイレンの音とともに出現する数々の謎に翻弄されながら、予測不能な怪異に巻き込まれる・・・。', '<a href="https://amzn.to/3YZ4Wc6" target="_blank"><i class="bi bi-shop"></i>Prime Video</a>', NULL, 0, NULL, '2024-11-22 06:15:29', '2025-10-06 08:22:57'),
	(43, 'silent-hill-movie', 1, 'サイレントヒル', 'サイレントヒル', NULL, 20, 0, 1, '最愛の娘・シャロンが、悪夢にうなされて叫ぶ「サイレントヒル…」という奇妙な言葉。\r\n母親のローズはその謎を解くため、シャロンを連れてウェストバージニア州に実在する街・サイレントヒルを訪ねる。\r\nだが、ひと気もなく、深い霧に覆われたその街は、一度足を踏み入れると抜け出すことのできない、呪われた迷宮だった。\r\nそこで忽然と姿を消してしまったシャロンを探すローズは、次々と想像を絶する恐怖に見舞われていく-。', '<a href="https://amzn.to/4hTCgtL" target="_blank"><i class="bi bi-shop"></i>Amazon</a>', NULL, 0, NULL, '2024-11-22 09:33:52', '2024-11-22 09:33:52'),
	(44, 'silent-hill-revelation', 1, 'サイレントヒル: リベレーション', 'サイレントヒル:<br>\r\nリベレーション', NULL, 20, 0, 2, '18歳の誕生日を迎えようとしているヘザーの人生は、他の同世代の女の子たちとはまったく違っていた。\r\n幼い頃の記憶がない彼女は、父親ハリーに守られるようにして各地を点々と移り住み、夜ごとサイレントヒルという謎の街でおぞましい“何か"に追われる悪夢にうなされているのだ。\r\nそんなある日、父親が不可解な失踪を遂げる。\r\n壁には「サイレントヒルに来い」という血文字が残されていた。\r\n遂にヘザーは不気味な深い霧と静寂に包まれたサイレントヒルに足を踏み入れてしまう、と突然鳴り響いたサイレンを合図に街は闇に飲み込まれ、この世のものとは思えない“何か"が姿を現す。\r\n父親を捜すため、さらに迷宮の奥深くへと身を投じたヘザーは、自らの封印された出生の秘密、そしてサイ レントヒルの呪われた真実を探りあてていくのだった・・・。', '<a href="https://amzn.to/3ZjKnZk" target="_blank"><i class="bi bi-shop"></i>Amazon</a>', NULL, 0, NULL, '2024-11-22 09:37:36', '2024-11-22 09:37:36'),
	(45, 'return-to-silent-hill', 1, 'Return to Silent Hill', 'Return to Silent Hill', 13, NULL, 0, 1, '2025年公開予定の実写映画。\r\nサイレントヒル2をベースとした物語が展開される。', NULL, NULL, 0, NULL, '2024-11-22 21:36:54', '2024-11-22 21:36:54');
