INSERT INTO `game_platforms` (`id`, `key`, `game_maker_id`, `name`, `acronym`, `node_name`, `type`, `sort_order`, `description`, `description_source`, `created_at`, `updated_at`) VALUES
	(3, 'windows', 11, 'Windows', 'Win', 'Windows', 4, 110, 'Windowsは1985年に最初にリリースされたパソコン用のオペレーティングシステム。\r\nアプリケーションを「ウィンドウ」単位で表示するというGUI機能により、それまでのパソコンの操作性を改善、世界中で利用されるOSとなった。\r\n\r\nなお、本サイトにおいてはディスクやダウンロードなどで個別に販売されているWindows用ホラーゲームをWindowsプラットフォームとし、SteamやDMM GamesなどのWindows上でも動作するが独自の配信プラットフォームで提供されているものはそれらのプラットフォームにしています。', NULL, '2012-03-25 12:40:53', '2025-10-10 01:18:43');
INSERT INTO `game_platforms` (`id`, `key`, `game_maker_id`, `name`, `acronym`, `node_name`, `type`, `sort_order`, `description`, `description_source`, `created_at`, `updated_at`) VALUES
	(4, 'famicom', 3, 'ファミコン', 'FC', 'ファミコン', 1, 2099, 'ファミリーコンピュータは1983年に発売されたハード。\r\nカートリッジ採用によるゲームソフトの交換の容易さ、十字キーを備えたコントローラーによる新しいゲーム操作などにより、家庭用ゲーム機として大成功を収めました。', NULL, '2012-03-25 12:40:53', '2024-07-13 20:32:17'),
	(5, 'super-famicom', 3, 'スーパーファミコン', 'SFC', 'スーパーファミコン', 1, 2097, 'スーパーファミコンは1999年に発売されたハード。\r\n2D性能の大幅向上など、ファミコンから正統な進化を遂げて大ヒットした。', NULL, '2012-03-25 12:40:53', '2024-07-13 20:35:53'),
	(7, 'playstation', 4, 'PlayStation', 'PS', 'PlayStation', 1, 1099, 'PlayStationは1994年に発売されたハード。\r\nCD-ROMを採用し、3DグラフィックスやCD音楽をサポートし、よりリアルなゲーム体験を提供した。', NULL, '2012-03-25 12:40:53', '2024-07-29 20:32:02'),
	(8, 'sega-saturn', 12, 'セガサターン', 'SS', 'セガサターン', 1, 4095, 'セガサターンは1994年に発売されたハード。\r\n2Dグラフィック性能が大幅に向上し、3Dの描画性能も持っていた。\r\nCD-ROM採用による大容量化、マルチプロセッサ採用によるCPU処理の高速化などを持っていた。', NULL, '2012-03-25 12:40:53', '2024-07-13 19:50:33'),
	(9, 'playstation2', 4, 'PlayStation2', 'PS2', 'PlayStation2', 1, 1098, 'PlayStation2は2000年に発売されたハード。\r\n3D描画性能が大幅に上がり、DVD-ROMの採用による大容量化も実現した。\r\nDVD再生機能も備えていたことが大ヒットの要因の1つともいわれている。', NULL, '2012-03-25 12:40:53', '2024-07-13 19:53:20'),
	(10, 'xbox', 11, 'Xbox', 'XB', 'Xbox', 1, 3099, 'Xboxは2002年に発売されたハード\r\nHDDやイーサネットなど、同世代の他ハードにはない先進の機能を標準で装備していました。', NULL, '2012-03-25 12:40:53', '2024-07-13 20:00:09'),
	(11, 'xbox360', 11, 'Xbox360', 'XB360', 'Xbox360', 1, 3098, 'Xbox360は2005年に発売されたハード。\r\nハイビジョンに対応し、より高解像度でリアルなグラフィックを提供した。\r\n「Xbox Live」による先進的なオンライン機能の提供により、オンラインプレイの普及に貢献した。', NULL, '2012-03-25 12:40:53', '2024-07-13 20:08:11'),
	(12, 'playstation3', 4, 'PlayStation3', 'PS3', 'PlayStation3', 1, 1097, 'PlayStation3は2006年に発売されたハード。\r\nHDDおよびネット接続機能の標準搭載など、オンラインプレイ機能が強化された。\r\nBlu-ray Discの採用による大容量化、「Cell」によるCPU性能の大幅強化、ワイヤレスコントローラーの採用など新しい機能も搭載された。', NULL, '2012-03-25 12:40:53', '2024-07-13 20:17:02'),
	(13, 'wii', 3, 'Wii', 'Wii', 'Wii', 1, 2094, 'Nintendo Wiiは2006年に発売されたハード。\r\n「Wiiリモコン」という新しいコントローラーの採用により、今までなかった新しいゲーム体験を提供した。\r\nまた独自のアバター機能「Mii」や古いゲームを遊べる「バーチャルコンソール」なども提供した。\r\nグラフィック性能は競合ハードに劣るものの、新しいゲーム体験が受け入れられ大ヒットした。', NULL, '2012-03-25 12:40:53', '2024-07-13 20:23:47'),
	(14, 'playstation-portable', 4, 'PlayStation Portable', 'PSP', 'PlayStation<br>\r\nPortable', 2, 1199, 'PlayStation Portableは2004年に発売された携帯ハード。\r\nPS2に近いグラフィック性能と多彩なマルチメディア機能を持っていた。\r\n「モンスターハンターポータブル」の人気も一因となり、大ヒットした。', NULL, '2012-03-25 12:40:53', '2025-10-10 01:18:43'),
	(15, 'nintendo-ds', 3, 'ニンテンドーDS', 'DS', 'ニンテンドーDS', 2, 2196, 'ニンテンドーDSは2004年に発売された携帯ハード。\r\n上下に2つの画面を持ち、下部のスクリーンは専用ペンでのタッチ操作に対応するという特徴を持つ。\r\nローカル通信、おすそわけプレイなどの新しい機能も搭載されていた。\r\nデザインや多様な機能から大ヒットした。', NULL, '2012-03-25 12:40:53', '2025-10-10 01:18:43'),
	(16, '3do', NULL, '3DO', '3DO', '3DO', 1, 10002, '3DOは1994年に発売されたハード。\r\nオープンハードウェアにより、複数の企業が3DOを販売することができた。\r\nスペックは高かったが高価格での販売となり、市場シェアを獲得できなかった。', NULL, '2012-03-25 12:40:53', '2024-07-14 20:57:26'),
	(17, 'dream-cast', 12, 'ドリームキャスト', 'DC', 'ドリームキャスト', 1, 4094, 'ドリームキャストは1998年に発売されたハード。\r\nグラフィック性能の向上、独自規格のGD-ROMの採用による大容量化など前世代「セガサターン」から順当な進化を遂げた。\r\n販売はかなり苦戦し、セガが発売する最後のハードとなった。', NULL, '2012-03-25 12:40:53', '2024-07-13 20:51:42'),
	(18, 'game-cube', 3, 'GameCube', 'GC', 'GameCube', 1, 2095, 'Nintendo GmaeCubeは2001年に発売されたハード。\r\n8cmミニDVDディスクの採用による大容量化やグラフィック性能の強化などが行われた。\r\nハードウェア性能は高かったが、市場シェアでは苦戦した。\r\nGameCube苦戦の経験から、グラフィック性能よりもゲーム体験を重視することへシフトし、次の「Wii」へつながったのかもしれない。', NULL, '2012-03-25 12:40:53', '2024-07-13 20:47:00'),
	(19, 'nintendo-64', 3, 'Nintendo 64', 'N64', 'Nintendo 64', 1, 2096, 'Nintendo 64は1996年に発売されたハード。\r\n64bitアーキテクチャを採用し、同世代ハードの中でも高い3D性能で滑らかな3Dゲーム体験を提供した。', NULL, '2012-03-25 12:40:53', '2024-07-29 20:32:02'),
	(20, 'gameboy-advance', 3, 'ゲームボーイアドバンス', 'GBA', 'ゲームボーイ<br>\r\nアドバンス', 2, 2197, 'ゲームボーイアドバンスは2001年に発売された携帯ハード。\r\nそれまでのゲームボーイが縦持ちだったのに対し、GBAから横持ちスタイルに変更された。\r\nゲームボーイおよびゲームボーイカラーと下位互換を持っていた。\r\nバッテリー電池化や折り畳みデザイン化されたゲームボーイアドバンスSP、また下位互換をなくしたゲームボーイミクロといった機種も発売された。\r\n全世界で8000万台を販売する大ヒットとなった。', NULL, '2012-03-25 12:40:53', '2025-10-10 01:18:43'),
	(21, 'wonder-swan', 19, 'ワンダースワン', 'WS', 'ワンダースワン', 2, 10004, 'ワンダースワンは1999年に発売された携帯用ハード。\r\n2000年にはカラー表示が可能となった「ワンダースワンカラー」も発売された。\r\n縦横両方でプレイ可能でゲームのスタイルに合わせて持ち方を変えてプレイするという特徴を持っていた。\r\n電池寿命が長く、一部のゲームファンからは高い評価を得ていたものの、販売は振るわなかった。', NULL, '2012-03-25 12:40:53', '2025-10-10 01:18:43'),
	(22, 'feature-phone', NULL, '携帯電話', '携帯電話', '携帯電話', 7, 10201, '携帯電話は電話以外にもゲームやインターネット機能を持った携帯ハード。\r\nフィーチャーフォンと呼ばれ、日本独自のガラケーとは意味合いが異なるようですが、当サイトではフィーチャーフォンもガラケーもこの「携帯電話」プラットフォームとして扱います。', NULL, '2012-03-25 12:40:53', '2025-10-10 01:18:43'),
	(23, 'gameboy-color', 3, 'ゲームボーイカラー', 'GBC', 'ゲームボーイカラー', 2, 2198, 'ゲームボーイカラーは1998年に発売された携帯ハード。\r\nカラー表示に対応し、ゲームボーイからスペックも強化された。\r\n短いライフスパンにも関わらず、世界的に大ヒットした。', NULL, '2012-03-25 12:40:53', '2025-10-10 01:18:43'),
	(24, 'famicom-disc-system', 3, 'ファミコンディスクシステム', 'FCディスクシステム', 'ファミコン<br>\r\nディスクシステム', 1, 2098, 'ファミコンディスクシステムは1986年に発売されたファミコンの周辺機器。\r\nカートリッジに比べ大容量化し、セーブ機能も対応した。\r\nまた、各地の小売店にディスクライターを設置し、ユーザー自身でゲームソフトを書きこむことができるようにした。', NULL, '2012-03-25 12:40:53', '2025-10-06 21:05:07'),
	(25, 'satteraview', 3, 'サテラビュー', 'サテラビュー', 'サテラビュー', 1, 10003, 'サテラビューは1995年に発売されたスーパーファミコン用の周辺機器。\r\n衛星通信を利用してゲームソフトをダウンロードすることができた。\r\nインターネットがない時代に通信を利用してソフトを配信することに挑んだ商品だった。', NULL, '2012-03-25 12:40:53', '2024-07-14 21:02:19'),
	(26, 'nintendo-3ds', 3, 'ニンテンドー3DS', '3DS', 'ニンテンドー3DS', 2, 2195, 'ニンテンドー3DSは2011年に発売された携帯ハード。\r\nDSの後継機で、上部スクリーンに裸眼での3D立体表示機能が搭載された。\r\nグラフィック性能の強化に加え、すれちがい通信やARなど新しい機能が搭載されていた。\r\n発売当初は販売が振るわず、発売から半年で1万円の値下げなどが実施された。\r\n値下げやプロモーション強化などにより、最終的には大ヒットした。\r\n\r\n（ホラーゲームネットワーク管理人は3DSのローンチタイトルの1つに名前が載っているらしい）', NULL, '2012-03-25 12:40:53', '2025-10-10 01:18:43'),
	(28, 'playstation-vita', 4, 'PlayStation Vita', 'Vita', 'PlayStation Vita', 2, 1198, 'PlayStation Vitaは2011年に発売された携帯ハード。\r\nPS3に匹敵する高いグラフィック性能を持ち、大型で高解像度の液晶を搭載することで、携帯ハードながら迫力のあるグラフィックを楽しめた。\r\n背面タッチパッド、3Gのモバイル通信機能などを搭載していた。', NULL, '2012-03-25 12:40:53', '2025-10-10 01:18:43'),
	(29, 'wii-u', 3, 'Wii U', 'WiiU', 'Wii U', 1, 2093, 'Wii Uは2012年に発売されたハード。\r\nタッチスクリーンを搭載した新しいコントローラーを採用し、Wiiの「Wiiリモコン」に次いで新しいゲーム体験を提供した。\r\nグラフィック性能の向上やネットワーク機能も強化されたが、販売は振るわなかった。', NULL, '2012-09-15 12:10:06', '2024-07-13 20:56:33'),
	(30, 'android', 109, 'Android', 'Android', 'Android', 7, 140, 'Androidは2008年に登場したモバイル向けOS。\r\nオープンソースのためカスタマイズが可能で、その柔軟性により多くのメーカーのモバイルデバイスに採用された。\r\n「Google Play」から公式に審査されたアプリが配信されているが、審査なしで自由にアプリを配信することもできる。', NULL, '2018-03-24 01:18:11', '2025-10-10 01:18:43'),
	(31, 'ios', 106, 'iOS', 'iOS', 'iOS', 7, 130, 'iOSは2007年に登場したモバイル向けOS。\r\nAppleが開発したOSで、iPhoneやiPadなど同社のモバイルデバイスにのみ搭載されている。\r\nアプリ配信ストアのApp Storeによって、厳格な審査プロセスによって一定の品質が保たれたアプリが提供されている。', NULL, '2018-03-24 01:18:11', '2025-10-10 01:18:43'),
	(32, 'new-nintendo-3ds', 3, 'new ニンテンドー3DS', 'new3DS', 'new ニンテンドー3DS', 2, 2194, 'new ニンテンドー3DSは2014年に発売された携帯ハード。\r\nスペックの強化やCスティックの追加、3Dブレ防止機能などが追加された。\r\n\r\n一般的には3DSの上位機種で同一プラットフォームという認識の方が多いと思いますが、3DSでは動作せずnew 3DSのみで動作するソフトが存在するため本サイトではプラットフォームを分けています。', NULL, '2018-03-24 01:18:11', '2025-10-10 01:18:43'),
	(33, 'playstation4', 113, 'PlayStation4', 'PS4', 'PlayStation4', 1, 1096, 'PlayStation4は2014年に発売されたハード。\r\nスペックの強化以外にも動画配信やモバイルデバイスを利用してもリモートプレイなどの機能を搭載していた。\r\nPS3と比べて初期型から小型で価格も抑えられていたこともあり、全世界で大ヒットした。\r\n2016年には4K出力などスペックを強化したPlayStation4 Proが発売された。\r\nまた、VRに対応する周辺機器のPlayStation VRも2016年に発売されている。', NULL, '2018-03-24 01:18:11', '2024-07-15 20:24:23'),
	(34, 'switch', 3, 'Nintendo Switch', 'Switch', 'Nintendo Switch', 1, 2092, 'Nintendo Switchは2017年に発売されたハード。\r\n据え置きと携帯の両方に対応し、家のテレビでも持ち出して外でもゲームを遊べるという特徴を持つ。\r\n新しいコントローラー「Joy-Con」やタッチスクリーンの採用でも新しいゲーム体験を提供した。\r\nグラフィック等のスペック面では競合ハードに劣っているものの、ウルトラ大ヒット。\r\n競合ハードが世代交代した後でもヒットが続き、任天堂ハードとしては最長の現役期間を誇るハードとなった。', NULL, '2018-03-24 01:18:11', '2024-07-13 21:06:46'),
	(35, 'xbox-one', 11, 'XBOX One', 'XB1', 'XBOX One', 1, 3097, 'Xbox Oneは2014年に発売されたハード。\r\nモーショントラッキングアクセサリのKinect 2.0を同梱し、クラウドコンピューティングなどに対応した。\r\n2015年にはSSHDや高品質なコントローラーを同梱した上位機種のXbox One Eliteが発売された。', NULL, '2018-03-24 01:18:11', '2024-07-14 20:54:07'),
	(36, 'dvd-pg', NULL, 'DVD-PG', 'DVD-PG', 'DVD-PG', 8, 10102, 'DVD-PGはDVDプレーヤーを使って操作するゲームプラットフォーム。\r\n特定のハードに依存せず、DVD再生機能があるハードであればプレイ可能だった。', NULL, '2018-03-24 01:20:05', '2025-10-10 01:18:43'),
	(37, 'pc-engine', NULL, 'PCエンジン', 'PCE', 'PCエンジン', 1, 10001, 'PCエンジンは1987年に発売されたゲーム機。\r\n家庭用ゲーム機として初めてCD-ROM拡張ユニットを導入した。\r\n小型設計で先進的なハードウェアを搭載していた。\r\n日本では人気を博したものの、北米では苦戦した。', NULL, '2018-03-24 01:22:05', '2024-07-15 20:30:03'),
	(38, 'umd-pg', NULL, 'UMD-PG', 'UMD-PG', 'UMD-PG', 8, 10101, 'UMD-PGはUMDビデオの再生機能を使ったゲームプラットフォーム。\r\nDVD-PGのUMDバージョン。', NULL, '2018-06-13 07:49:09', '2025-10-10 01:18:43'),
	(39, 'game-archives', 4, 'ゲームアーカイブス', 'ゲームアーカイブス', 'ゲームアーカイブス', 5, 9001, 'ゲームアーカイブスは2006年に開始された、古いゲームを購入・プレイできるサービス。\r\nPS3・PSP・PS VItaに対応し、PSやPS2、PCエンジンなどのゲームが配信された。\r\n2021年にサービス終了のアナウンスがされたがユーザーの反発により撤回された。\r\nPSPのみサービス終了している。', NULL, '2018-06-28 02:25:28', '2025-10-10 01:18:43'),
	(40, 'virtual-console', 3, 'バーチャルコンソール', 'VC', 'バーチャルコンソール', 5, 9002, 'バーチャルコンソールは2006年に始まった古いゲームを購入・プレイできるサービス。\r\nWii・3DS・Wii Uでファミコンやニンテンドー64などのソフトを遊ぶことができた。\r\n2023年に新規ダウンロードが停止された。', NULL, '2018-06-28 02:25:28', '2025-10-10 01:18:43'),
	(41, 'xbox-live', 11, 'Xbox Live', 'XBL', 'Xbox Live', 5, 9003, 'Xbox Liveは2006年にスタートしたオンラインマルチプレイおよびゲームソフト配信サービス。\r\nXbox 360以降のハードおよびWindows PCに対応している。\r\n対応ソフトのオンラインマルチプレイやゲームソフトの購入、コミュニティ機能などを提供している。', NULL, '2018-06-28 02:25:28', '2025-10-10 01:18:43'),
	(43, 'xbox-series-xs', 11, 'XBOX Series X|S', 'XBX|S', 'XBOX<br>\r\nSeries X/S', 1, 3096, 'Xbox Series X および Xbox Series S は2020年に発売されたハード。\r\nXは4K、最大120FPSのフレームレート、高速SSDなど最新の性能を持つハード。\r\nSは1440pの解像度で最大フレームレート120、ディスクドライブ非搭載で性能を落としサイズと価格を抑えたハードとなっている。\r\nどちらのハードでも遊べるソフトは変わらない。', NULL, '2024-03-07 09:54:23', '2024-10-13 06:47:34'),
	(44, 'playstation5', 4, 'PlayStation5', 'PS5', 'PlayStation5', 1, 1095, 'PlayStation5は2020年に発売されたハード。\r\n4Kおよび一部ゲームで8Kの解像度に対応し、さらにレイトレーシング対応により、よりリアルなグラフィック表現が可能となった。\r\n最大120FPSのフレームレートに対応、超高速SSDを採用によるゲームロード時間の大幅短縮、DualSenceコントローラーによる新しいゲーム操作体験などを提供した。', NULL, '2024-03-07 09:57:48', '2024-07-13 20:57:19'),
	(45, 'steam', 162, 'Steam', 'Steam', 'Steam', 5, 100, 'Steamは2003年に始まったゲームの配信プラットフォーム。\r\nAAAタイトルからインディタイトルまで、数多くのゲームが配信されている。\r\n多彩なコミュニティ機能もあり、世界中で多くのユーザーに利用されている。\r\n主にPCでの利用となるが、独自ハードも販売している。', NULL, '2024-03-07 10:07:38', '2025-10-10 01:18:43'),
	(46, 'gog', 196, 'GOG.com', 'GOG', 'GOG.com', 5, 101, 'GOG.comは2008年にサービスを開始したゲーム配信プラットフォーム。\r\nDRMフリーで提供されることが特徴。\r\n最新作からレトロゲームまで多彩なゲームを配信しているが、特に古いゲームを現代でも動作するように修正してリリースすることに注力している。', NULL, '2024-07-15 20:36:07', '2025-10-10 01:18:43'),
	(47, 'project-egg', NULL, 'プロジェクトEGG', 'EGG', 'プロジェクトEGG', 5, 102, 'プロジェクトEGGは2001年にサービスを開始したオンライン配信サービス。\r\nレトロゲームを配信していることが特徴。', NULL, '2024-07-15 20:44:52', '2025-10-10 01:18:43'),
	(48, 'dmm-games', 108, 'DMM GAMES', 'DMM GAMES', 'DMM GAMES', 5, 0, '', NULL, '2024-10-18 20:18:23', '2025-10-10 01:18:43'),
	(49, 'fanza-games', 108, 'FANZA GAMES', 'FANZA GAMES', 'FANZA GAMES', 5, 0, '', NULL, '2024-10-18 20:18:50', '2025-10-10 01:18:43'),
	(50, 'epic-games-store', 217, 'Epic Games Store', 'Epic', 'Epic Games Store', 5, 20181206, 'Epic Gamesが展開するPCゲーム用のオンライン配信プラットフォーム。\r\n時限での独占配信や定期的にゲームの無料配信が行われている。', NULL, '2024-10-19 21:11:59', '2025-10-10 01:18:43'),
